package com.excelon.app;

public class Operators1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num1 = 10,num2 = 3;
System.out.println("num1 + num2 = "+(num1 + num2));
System.out.println("num1 - num2 = "+(num1 - num2));
System.out.println("num1 * num2 = "+(num1 * num2));
System.out.println("num1 / num2 = "+(num1 / num2));
System.out.println("num1 % num2 = "+(num1 % num2));
	}

}
